member.remarks:
代码列子：

//XML代码
<Button Content="{Binding Txt}"/>

//在窗体中设置绑定数据源
this.DataContext = new MainWindowViewModel()

//ViewModel类
public class MainWindowViewModel : BindableBase
{
    private string _Txt = "按钮";
    public string Title
    {
        get { return _Txt; }
        set { SetProperty(ref _Txt, value); }
    }
}

代码列子：

//XML代码
<Button Command="{Binding MyCommand}" Content="按钮"/>

//在窗体中设置绑定数据源
this.DataContext = new MainWindowViewModel()

//ViewModel类
public class MainWindowViewModel : BindableBase
{
    public ICommand MyCommand { get => new DelegateCommand(My); }
    //执行方法
    public void My()
    {
        //code
    }
}

代码列子：

//XML代码
<Button Command="{Binding MyCommand}" CommandParameter="abc" Content="按钮"/>

//在窗体中设置绑定数据源
this.DataContext = new MainWindowViewModel()

//ViewModel类
public class MainWindowViewModel : BindableBase
{
    public ICommand MyCommand { get => new DelegateCommand<string>(My); }
    //执行方法
    public void My(string obj)
    {
        //code
    }
}
